function menu:update()
  if btn(2) and self.pressed >= self.timeout then
    self.curs -= 1
    self.pressed = 0
  end
  if btn(3) and self.pressed >= self.timeout then
    self.curs += 1
    self.pressed = 0
  end
  if self.curs <= 0 then
    self.curs = #self.list
  end
  if self.curs > #self.list then
    self.curs = 1
  end
  if self.pressed < self.timeout then self.pressed += 1
  end
  
  if btn(4) then
    if self.curs == 1 then
      game_init()
		    update = game_update
		    draw = game_draw
		    music(-1, 600)
    end
  end
end

