function rot2d(x,y,a)
 local x0=x
 x = cos(a)*x - sin(a)*y
 y = cos(a)*y + sin(a)*x0
 return x,y
end

function draw_3d()
 local celreach = {}
 local celz0
 local col
 local check = {}
 -- calculate view plane (line)

 local v={}
 v.x0 = cos(pl.d+pl.fov) 
 v.y0 = sin(pl.d+pl.fov)
 v.x1 = cos(pl.d-pl.fov)
 v.y1 = sin(pl.d-pl.fov)
 
 for sx=0,127 do

  sy=127

  -- camera based on player pos
  x=pl.x
  y=pl.y
  z=pl.z-1 -- 1 unit high -- player height

  ix=flr(x)
  iy=flr(y)
  tdist=0
  col=mget(ix,iy)
  celz=mz(ix,iy)
  
  -- calc cast vector
  local t=sx/127
  vx = v.x0 * (1-t) + v.x1 * t
  vy = v.y0 * (1-t) + v.y1 * t
  dir_x = sgn(vx)
  dir_y = sgn(vy)

  skip_x = 1/abs(vx)
  skip_y = 1/abs(vy) 
	 
  --come back to this
  if (sgn(vx) > 0) then
   dist_x = 1-(x%1) else
   dist_x =   (x%1) end
  if (sgn(vy) > 0) then
   dist_y = 1-(y%1) else
   dist_y =   (y%1) end

  dist_x = dist_x * skip_x
  dist_y = dist_y * skip_y
	
  -- start skipping
  skip=true
  --skips=0

  while (skip) do
	local temp = hash(ix,iy)
   	if(hash_table[temp] != nil and tdist > 0.1 and check[temp] != true) then
		add(celreach,hash(ix,iy))
		check[temp] = true
	end
   --skips = skips + 1
   if (dist_x < dist_y) then
    ix=ix+dir_x
    last_dir = 0
    dist_y = dist_y - dist_x
    tdist = tdist + dist_x
    dist_x = skip_x
   else
    iy=iy+dir_y
    last_dir = 1
    dist_x = dist_x - dist_y
    tdist = tdist + dist_y
    dist_y = skip_y
   end
	
   -- prev cel properties
   col0=col
   celz0=celz
   -- new cel properties
   celz = mz(ix,iy)
   col=mget(ix,iy)
   --celz=16-col*0.2


--   print(ix.." "..iy.." "..col)
    
   if (col==15) then skip=false end

   --discard close hits
   --if (tdist > 0.1 and tdist < 10) then
   if (tdist > 0.1) then
   -- screen space

   local sy1 = celz0-z
   sy1 = (sy1 * 64)/tdist
   sy1 = sy1 + 64 -- horizon 

   -- draw ground to new point
   if (sy1 < sy) then
    --line(sx,sy1-1,sx,sy,col0)
    -- floor color
    gcol=13
    if(celz0 < 16) then gcol=6 end
    line(sx,sy1-1,sx,sy,gcol)
    sy=sy1
   end
			
			local texx = texx(last_dir,ix,iy,dir_x,dir_y,y,x,vy,vx)
    -----       texx(side,mapx,mapy,stepx,stepy,rayposy,rayposx,raydiry,raydirx)
   local texy= flr(texx/127)*16
   -- draw wall if higher
   
   if (celz < celz0) then
    local sy1 = celz-z
    sy1 = (sy1 * 64)/tdist
    sy1 = sy1 + 64 -- horizon 
    if (sy1 < sy) then
     --line(sx,sy1-1,sx,sy,last_dir*1+6)
     --flr(16+sx%16)
     palt(0,false)
     sspr(
     	0+texx,96+texy,
     	1,32, -- sw,sh
     	sx,sy1-1,
     	1,sy-sy1+1
     )
     palt()
     sy=sy1
    end
   end
	
  end 
  end -- skipping
 end -- sx

