function make_car(cx,cy,cz,cw,ch,cspr)
 car = { 
  x = cx, y=cy,z=cz,
  w=cw, h=ch, sp=cspr,
  radius = 1,
  update = function(c)
  	if(pl.vehicle_mode) then
  		c.x = pl.x
  		c.y = pl.y
  		c.z = pl.z
  	else
	  	local x =c.x-pl.x
	  	local y =c.y-pl.y
	  	local t = sqrt((x*x) + (y*y))
	  	if(t< c.radius) then
	  		pl.hop = true
	  	else 
	  		pl.hop = false
	  	end
  	end
  end
 }
 return car
end

