function shoottest()
for obj in all(objtable.enemies_list)do
    local cx,cy = rot2d(obj.x-pl.x,obj.y-pl.y,-(pl.d-0.25))
		 	cy = -cy 
		 	local cz  = obj.z - (pl.z-pl.h)
			 local sx= 64+cx*128/cy
			 local sy= 64+cz*64/cy
			 local w = obj.w / cy
			 local h = obj.h / cy
			 sx = 	sx-w/2
			 sy = sy - h
	 if(sx < crosshair.x + crosshair.w
	 			 and sx + w > crosshair.x 
	 			   and sy < crosshair.y + crosshair.h
	 			     and sy + h > crosshair.y) then
	   obj.hit = true
	   obj.hp-=10
	 end	     
	end
end

function gr_update()
	
	for gr in all(objtable.gr_list) do
		--gr.update()
		-------------------------------
			  gr.timer =gr.timer - 1
		   local m= gr.speed
		   local dx = cos(gr.d)*m
		   local dy = sin(gr.d)*m
		   if (mz(gr.x+dx, gr.y+dy) > 
						gr.z) then --check if wall is lower
						 gr.x+=dx
						 gr.y+=dy
		   end
				 -- z means player feet
				 if (gr.z >= mz(gr.x,gr.y) and gr.dz >=0) then
				  gr.z = mz(gr.x,gr.y)
				  gr.dz = -gr.dz/2 -- reset jump
				 else
				  gr.dz=gr.dz+ gravity -- gravity
				  gr.z =gr.z + gr.dz
				 end
		if(gr.timer <= 0)then
		 -- animate grenade here
		 gr.explode(gr)
		 del(objtable.gr_list,gr)
		end
	end
end

function throw_grenade()
	if(grenadetimer <= 0) then
		local g =make_grenade(pl.x,pl.y,pl.z,pl.d)
		add(objtable.gr_list,g)
		grenadetimer = grenadedelay
	end
end


function make_grenade(gx,gy,gz,gd)
 local gr = {
	 x=gx,y=gy,z=gz, 
	 d=gd,dz= -0.25,
	 speed=0.2,
	 timer=50,
	 w = 10, h=10,
	 radius = 3,
	 sp = 3,
	 explode = function (self)
	 	for en in all(objtable.enemies_list) do
	 		local dx = en.x-self.x
	 		local dy = en.y-self.y
	 		dx*=dx dy*=dy
	 		if(sqrt(dx+dy) < self.radius) then
	 			en.hp -= 80
	 			en.hit = true
	 		end
	 	end
	 end
 }
 return gr
end



function texx(side,mapx,mapy,stepx,stepy,rayposy,rayposx,raydiry,raydirx)
			local wallx = 0
			local texwidth = 16
			-- work out the wall x fraction
   if(side==0)then
     perpwalldist=(mapx-rayposx+(1-stepx)/2)/raydirx
     wallx=rayposy+perpwalldist*raydiry
   else
   		perpwalldist=(mapy-rayposy+(1-stepy)/2)/raydiry
     wallx=rayposx+perpwalldist*raydirx
   end
   wallx-=flr(wallx)
   local texx=flr(wallx*texwidth)
   if(side==0 and raydirx>0)then
   	texx=texwidth-texx-1
  	end
  	if(side==1 and raydiry<0)then
   	texx=texwidth-texx-1
  	end
   --texx+=tile*texwidth
   texx%=127
   return texx
end
