-- draw a sprite in the 3d world
function drawspriteworld(index, sx, sy, w,h)
 local sprx=spritedata[index][1]
 local spry=spritedata[index][2]
 local sprw=spritedata[index][3]
 local sprh=spritedata[index][4]
 palt(0,false)
 palt(14,true)
 sspr(sprx,spry,sprw,sprh,sx-w/2,sy-h,w,h)
	palt(0, true)
end

function drawobjects(objects)
 palt(0,true)
 for index in all(objects) do
	for a in all(hash_table[index]) do	
		 local cx = 0
		 local cy = 0
		 cx,cy = rot2d(a.x-pl.x,a.y-pl.y,-(pl.d-0.25))
		 cy = -cy
		 if(abs(cy) >  0.1) then
			 local cz  = a.z - (pl.z-pl.h)
			 local sx= 64+cx*128/cy
			 local sy= 64+cz*64/cy
			 local w = a.w / cy
			 local h = a.h / cy
			 local sp = a.sp
			 if(a.hit == true)then
			  pal(7,8)
			 end
			drawspriteworld(sp,sx,sy,w,h)
			pal()
			if(a.hit != nil)then
			 a.hit = false
			end
		 end
	end
 end
end

function update_objecttable()
end

function update_hashtable(objtable)
	local hash_table ={}
	for key,otable in pairs(objtable) do
		for value in all(otable) do
			local xy = worldtocell(value.x,value.y)
			local index = hash(xy[1],xy[2])
			local bucket = hash_table[index] or {}
			add(bucket,value)
			hash_table[index] = bucket
		end
	end
	return hash_table
end

function hash(ix,iy)
	return 127*iy+ix
end

function worldtocell(x,y)
	local i = flr(x)
	local j = flr(y)
	return {i,j}
end

